import React, { useState } from "react";
import { Story, Meta } from "@storybook/react";
import Checkbox, { CheckboxProps } from "../../Atoms/checkbox";

export default {
  title: "Atoms/Checkbox",
  component: Checkbox,
  argTypes: {
    label: { control: "text" },
    checked: { control: "boolean" },
    disabled: { control: "boolean" },
   
  },
} as Meta;

const Template: Story<CheckboxProps> = (args) => {
  const [checked, setChecked] = useState<boolean>(args.checked || false);
  const [disabled, setDisabled] = useState<boolean>(args.disabled || false);

  const handleOnChange = () => {
    const newValue = !checked;
    setChecked(newValue);
    args.onChange && args.onChange(newValue);
  };

  return (
    <Checkbox
        {...args}
        checked={args.checked !== undefined ? args.checked : checked}
        onChange={handleOnChange}
        disabled={args.disabled}
      />
  );
};

export const Default = Template.bind({});
Default.args = {
  label: "Checkbox",
  checked: false,
  disabled: false,
  
};


export const Checked = Template.bind({});
Checked.args = {
  label: "Checked Checkbox",
  checked: undefined,
};

export const Disabled = Template.bind({});
Disabled.args = {
  label: "Disabled Checkbox",
  disabled: true,
};

export const CheckedDisabled = Template.bind({});
CheckedDisabled.args = {
  label: "Checked Disabled Checkbox",
  checked: true,
  disabled: true,
};
